// src/pages/OrderConfirmationPage.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { orderService } from '../api/apiService';

const OrderConfirmationPage = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    async function fetchOrderDetails() {
      try {
        setLoading(true);
        const orderData = await orderService.getOrderById(orderId);
        setOrder(orderData);
      } catch (err) {
        setError('Failed to load order details.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    }
    
    if (orderId) {
      fetchOrderDetails();
    }
  }, [orderId]);
  
  if (loading) return <div className="loading">Loading order details...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!order) return <div className="error">Order not found.</div>;
  
  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };
  
  return (
    <div className="order-confirmation-page">
      <div className="confirmation-header">
        <h1>Order Confirmed!</h1>
        <p className="success-message">
          Thank you for your order, {order.name}!
        </p>
      </div>
      
      <div className="order-details">
        <div className="detail-row">
          <span>Order Number:</span>
          <span>#{order.id}</span>
        </div>
        
        <div className="detail-row">
          <span>Order Date:</span>
          <span>{formatDate(order.created_at)}</span>
        </div>
        
        <div className="detail-row">
          <span>Status:</span>
          <span className="status">{order.status}</span>
        </div>
        
        <div className="detail-row">
          <span>Customer:</span>
          <span>{order.name}</span>
        </div>
        
        <div className="detail-row">
          <span>Phone:</span>
          <span>{order.phone}</span>
        </div>
      </div>
      
      <div className="ordered-items">
        <h2>Order Items</h2>
        <div className="items-list">
          {order.items.map((item, index) => (
            <div key={index} className="order-item">
              <div className="item-details">
                <span className="item-quantity">{item.quantity}x</span>
                <span className="item-name">{item.item_name}</span>
              </div>
              <span className="item-price">
                ${(item.price * item.quantity).toFixed(2)}
              </span>
            </div>
          ))}
        </div>
        
        <div className="order-total">
          <span>Total:</span>
          <span>${order.total_amount.toFixed(2)}</span>
        </div>
      </div>
      
      <div className="pickup-info">
        <h2>Pickup Information</h2>
        <p>Your order will be ready for pickup in approximately 20-30 minutes.</p>
        <p>Please show your order number when you arrive at The Digital Diner.</p>
      </div>
      
      <div className="confirmation-actions">
        <Link to="/menu" className="btn btn-primary">
          Order More Food
        </Link>
        <Link to="/order-history" className="btn btn-secondary">
          View Order History
        </Link>
      </div>
    </div>
  );
};

export default OrderConfirmationPage;
